<?php//include filesinclude_once('classes/Product.php');include_once('classes/Cart.php');//create displayCart variable set to empty string$displayCart = "";//The products array is an associative array that holds three objects.  Created from the Product class.$products = array(    1=>new Product(1, "SuperWidget", 19.99),    2=>new Product(2, "MegaWidget", 29.99),    3=>new Product(3, "WonderWidget", 39.99));//create a Cart object that is a instantiation of the Cart class$Cart = new Cart();//add the products array to the cart$Cart->addProductsArray($products);//starts session of one has not already been started. If so then uses existing sessionsession_start();//If there is no session array of cart then create one.if (!isset($_SESSION["cart"])){    $_SESSION["cart"] = array();    }//if get statement passes "addItem" then add item to sessionif (isset($_GET['action']) and $_GET['action'] == "addItem"){    $Cart->addItem();}//if get statement passes "removeItem" then remove item from sessionelse if(isset($_GET['action']) and $_GET['action'] == "removeItem"){    $Cart->removeItem();}//display cart, this is the default.else{    $displayCart = $Cart->displayCart();}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><meta name="Author" content="Your name" /><meta name="description" content="Learning PHP and MySQL" /><meta name="keywords" content="PHP, MySQL" /><meta name="Language" content="english" /><title>Form Validation</title></head><body>  	<?php echo $displayCart; ?> </body></html>