<?php //include filesinclude_once('classes/Validation.php');//add empty values to array in order to avoid error notices.$errorArray = array('','');//if submit button is clicked do the following.if (isset($_POST['submit'])){		//initiate object of the class	$Validation = new Validation();	//validate post values	$errorArray[0] = $Validation->checkForBlanks($_POST['username']);	$errorArray[1] = $Validation->checkForBlanks($_POST['password']);		//check for any errors	$error = $Validation->checkErrors($errorArray);		//if no errors found check username and password	if (!$error)	{		//check to make sure username and password are correct.                if ($_POST['username'] == "admin"  && $_POST['password'] == "Password1")                {                     grantAccess();                }                else if ($_POST['username'] == "user" && $_POST['password']=="Password1")                {                    grantAccess();                }                else                {                    $errorArray[0] = " Wrong username and/or password";                }  	}}//start session and grant appropriate access.function grantAccess(){    //must be on every page that uses this session    session_start();    //sets the access level     $_SESSION["accessLevel"] = $_POST['username'];     header("Location:page1.php");}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><meta http-equiv="Content-Type" content="text/html; charset=utf-8" /><title>Sessions</title><style type="text/css">.error{font-size: 11px; color: #F00;}</style></head><body><p>Username is "admin" or "user"</p><p>Password is "Password1"</p>  <form action="index.php" method="post">      <p><label for="username">Username:</label><span class="error"><?php echo $errorArray[0]; ?></span><br />          <input type="text" name="username" id="username" value="<?php if(isset($_POST['username'])){echo $_POST['username'];} ?>" /></p>      <p><label for="password">Password:</label><span class="error"><?php echo $errorArray[1]; ?></span><br />          <input type="password" name="password" id="password" /></p>       <p><input type="submit" name="submit" value="Login" /></p> </form></body></html>